import React from 'react';

const now = new Date();

const daysOfWeek = [
  'Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'
];

const dayToday = daysOfWeek[now.getDay()];

switch(dayToday) {
  case 'Monday':
    var tasks = [1,2,3,4,5];
    break;

  case 'Tuesday':
    var tasks = [1,2,3,4,5];
    break;

  case 'Wednesday':
    var tasks = [1,2,3,4,5];
    break;

  case 'Thursday':
    var tasks = [1,2,3,4,5];
    break;

  case 'Friday':
    var tasks = [1,2,3,4,5];
    break;

  case 'Saturday':
    var tasks = [1,2,3,4,5];
    break;

  case 'Sunday':
    var tasks = [1,2,3,4,5];
    break;

  default:
    var tasks = [1,2,3,4,5];
}


//converts tasks into a list 
const updatedTasks = tasks.map( (task)=> {
    return(
      <div class="wrapper">
        <input id="{task}" type="checkbox" />
        <label for="{task}">{task}</label>
      </div>
      )
});

function List() {
  return(
    <div>
      {updatedTasks}
    </div>
  );
}

export default List;
